import React, { createContext, useReducer } from 'react';
import Reducer from './Reducer';
import axios from 'axios';

// Initial state
const initialState = {
    transactions: [], // initially, an empty transaction List
    error: null,
    load: true,
}

// Create context
export const GlobalContext = createContext(initialState);

// Provider component to allow the components to receive the current state and actions
export const GlobalProvider = ({ children }) => {
    const [state, dispatch] = useReducer(Reducer, initialState); // dispatch calls the Reducer

    async function getTransactions() {
        try {
            const res = await axios.get('http://localhost:5000/api/v1/transactions');
            dispatch({
                type: "get_transactions",
                payload: res.data.data,
            });
        } catch (err) {
            dispatch({
                type: "error_request",
                payload: err.message,
            });
        }
    }

    // dispatch action
    const deleteTransaction = (id) => {
        // the object is the action
        dispatch({
            type: "delete_transaction",
            payload: id
        });
    }

    const addTransaction = (transaction) => {
        dispatch({
            type: "add_transaction",
            payload: transaction
        });
    }

    // provide the state or the functions accessible to all of the components
    return (
        <GlobalContext.Provider value = {
            {
                transactions: state.transactions,
                error: state.error,
                load: state.load,
                getTransactions,
                deleteTransaction,
                addTransaction,
            }
        }>
            {children}
        </GlobalContext.Provider>
    )
}
